    private void actionCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_actionCadastrarActionPerformed
        String nome = campoNome.getText();
        String usuario = campoCriarUsuario.getText();
        String senha = campoCriarSenha.getText();
        int idade = Integer.parseInt(campoIdade.getText());
        int altura = Integer.parseInt(campoAltura.getText());
        int peso = Integer.parseInt(campoPeso.getText());
        
        
        ConexaoBD conn = new ConexaoBD(nome, idade, altura, peso);
        int cadastro = conn.cadastrar(nome, idade, altura, peso);
        System.out.println("Resultado: " + cadastro);
        
        ConexaoBD login = new ConexaoBD(usuario, senha);
        int resultadoLogin = login.ArmazenaLogin(usuario, senha);
        System.out.println("Resultado: "+resultadoLogin);

        if (cadastro==1 && resultadoLogin==1)
        {
                JOptionPane.showMessageDialog(null, "Seu cadastro foi armazenado com sucesso!");
                this.dispose();
                
        }
        else {
                JOptionPane.showMessageDialog(null, "Ocorreram problemas ao salvar o registro");

        }
        
    }//GEN-LAST:event_actionCadastrarActionPerformed

